sign_staus = "SELECT t4.is_sign_in,t2.order_status AS request_status,t1.order_status AS work_status,t3.work_order_state,t3.request_order_status,t3.current_stage_code,t3.order_status,t4.task_status FROM rdp_fsm.r_work_order t1 LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id  AND t1.request_order_id = t3.request_order_id LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id  AND t3.request_order_id = t4.request_order_id  AND t3.task_order_id = t4.id  where  t2.id='%s'"
# 获取签到后
restule = "select t1.id,t1.request_order_id  from rdp_fsm.r_task_order t1 LEFT JOIN  rdp_fsm.r_order t2 on t1.id=t2.task_order_id where t1.is_sign_in='Y' and t2.current_stage_code='IN_SERVICE' and t1.task_status='FSMTOS02' and (t1.engineer_id)=(SELECT id from rdp_system.r_user where phone='18810007982') ORDER BY t1.sign_in_time  DESC  LIMIT 0,1"
# 验证正常完成时状态
result_normal = "SELECT t1.order_status AS work_status,t2.order_status AS request_order_status,t3.work_order_state,t3.task_order_state,t3.order_status,t3.request_order_status,t3.current_stage_code,t3.task_operation_type,t4.task_status,t4.operation_type FROM rdp_fsm.r_work_order t1  LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id  AND t1.request_order_id = t3.request_order_id LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id AND t3.request_order_id = t4.request_order_id AND t3.task_order_id = t4.id WHERE  t4.id = '%s'"
