# 查询所有可申请延迟的单
delayed_fil = "SELECT t1.task_order_id,t1.request_order_id FROM  rdp_fsm.r_work_order t1  LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id  LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id AND t1.request_order_id = t3.request_order_id LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id AND t3.request_order_id = t4.request_order_id AND t3.task_order_id = t4.id LEFT JOIN rdp_fsm.r_processed_task t5 ON t4.request_order_id = t5.request_order_id WHERE  ( t4.engineer_id )=(SELECT id FROM  rdp_system.r_user t6 WHERE t6.phone = '18810007982')   and  t3.create_time >'2022-09-10'   and t1.order_status='FSMWOS03'  and t2.order_status='FSMROS02' and  t3.work_order_state='FSMWOS03' and t3.request_order_status='FSMROS02'  and t3.order_status='FSMIOS01' and  t3.is_sign_in='N'  and t4.task_type='FSMTT01'  ORDER BY t3.report_time DESC"
# 延迟申请后状态验证
delay_fil_state = "SELECT t1.order_status,t1.operation_type,t2.order_status,t2.operation_status,t3.work_order_state,t3.request_order_status,t3.order_status ,t3.is_sign_in,t4.task_type FROM  rdp_fsm.r_work_order t1  LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id  LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id AND t1.request_order_id = t3.request_order_id LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id AND t3.request_order_id = t4.request_order_id AND t3.task_order_id = t4.id LEFT JOIN rdp_fsm.r_processed_task t5 ON t4.request_order_id = t5.request_order_id WHERE  ( t4.engineer_id )=(SELECT id FROM  rdp_system.r_user t6 WHERE t6.phone = '18810007982') and t1.request_order_id='%s'"

#查询所有可申请转单的工单
transfer_order="select t1.id as work_order_id from rdp_fsm.r_work_order t1 LEFT JOIN  rdp_fsm.r_order t2 on t1.request_order_id=t2.request_order_id LEFT JOIN  rdp_fsm.r_task_order t3 on t3.request_order_id=t2.request_order_id where t1.order_status='FSMWOS02' and  t1.supplier_id='1578553284442013697'  and t1.has_transfer_order=0 group by t1.id  ORDER BY t1.update_time DESC "
# 验证转单数据状态显示
transfer_result="select  t1.order_status,t1.is_delete,t1.has_transfer_order,t2.request_order_status,t2.current_stage_code from rdp_fsm.r_work_order t1 LEFT JOIN  rdp_fsm.r_order t2 on t1.request_order_id=t2.request_order_id LEFT JOIN  rdp_fsm.r_task_order t3 on t3.request_order_id=t2.request_order_id where t1.id='%s'"
# 门店转单审核
transfer_handle="select  t1.id  as work_order_id ,t2.id as processed_task_id ,t3.id as transferOrderApplyId from rdp_fsm.r_work_order t1 LEFT JOIN  rdp_fsm.r_processed_task t2 on t1.id=t2.work_order_id LEFT JOIN rdp_fsm.r_transfer_order_apply t3 on  t2.request_order_id=t3.request_order_id  where   t1.order_status='FSMWOS02' and t1.supplier_id='1578553284442013697'  and t1.has_transfer_order=1    group by t1.id ORDER BY t1.dead_line DESC"



# 查询所有延迟单审核
delay = "SELECT t1.request_order_id,t5.id AS processedTaskId  FROM rdp_fsm.r_work_order t1 LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id  AND t1.request_order_id = t3.request_order_id  LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id AND t3.request_order_id = t4.request_order_id AND t3.task_order_id = t4.id  LEFT JOIN rdp_fsm.r_processed_task  t5 on t4.request_order_id=t5.request_order_id WHERE t1.order_status = 'FSMWOS03' AND t1.operation_type='FSMWOSCS04' AND t2.order_status = 'FSMROS03' AND t2.operation_status='FSMROS03002'  AND t3.work_order_state = 'FSMWOS03' AND t3.request_order_status = 'FSMROS03' AND ( t4.engineer_id )=(SELECT id FROM rdp_system.r_user t5 WHERE  t5.phone = '18810007982') AND t3.order_status = 'FSMIOS01' ORDER BY t3.report_time DESC"
# 查询延迟审批后的状态
delay_state = "SELECT t1.order_status,t1.operation_type,t2.order_status,t2.operation_status,t3.work_order_state,t3.request_order_status,t3.order_status FROM  rdp_fsm.r_work_order t1  LEFT JOIN rdp_fsm.r_request_order t2 ON t1.request_order_id = t2.id  LEFT JOIN rdp_fsm.r_order t3 ON t1.id = t3.work_order_id AND t1.request_order_id = t3.request_order_id LEFT JOIN rdp_fsm.r_task_order t4 ON t3.work_order_id = t4.work_order_id AND t3.request_order_id = t4.request_order_id AND t3.task_order_id = t4.id LEFT JOIN rdp_fsm.r_processed_task t5 ON t4.request_order_id = t5.request_order_id WHERE  ( t4.engineer_id )=(SELECT id FROM  rdp_system.r_user t6 WHERE t6.phone = '18810007982') and t1.request_order_id='%s'"

# 获取所有未报价
quote_offer = "SELECT  t1.id AS fee_quote_id,t1.request_order_id,t1.work_order_id, t5.id AS task_order_id  FROM rdp_fsm.r_fee_quote t1 LEFT JOIN rdp_fsm.r_fee_task_summary t2 ON t1.id = t2.quote_id LEFT JOIN rdp_fsm.r_work_order t3 ON t1.work_order_id = t3.id LEFT JOIN rdp_fsm.r_supplier_rel_user t4 ON t3.supplier_id=t4.sup_id  LEFT JOIN  rdp_fsm.r_task_order t5 on t3.request_order_id=t5.request_order_id  where   t1.quote_state='NO_QUOTE'   and   (t4.user_id)=(select id from rdp_system.r_user where phone='18810007982' )  and t1.effect_flag=1  and t4.user_type='engineer'  AND t1.create_time > '2022-09-01'  GROUP BY t1.quote_code"
